import React, { Component } from 'react';
import axios from "axios";

class callUser extends Component {
    state = { 
        loading: true,
        people: [],
     }

    //  best place to put APi calls as it doesnt render first
     async componentDidMount(){
         const url = "https://api.randomuser.me/?results=5";
         await axios.get(url)
        //  then also returns a Promise making it chainable.
         .then(response=>{
             console.log(response.data.results);
             this.setState({person: response.data.results,loading: false});
         })


    //     console.log(response.data.results[0]);
         console.log(this.state.person.name.first);



     }
    render() { 
        const {loading, people } = this.state
        return (
            <div>
            {loading || !people ?
                 <div>loading...</div> :

                  <div>
                    <div>{this.state.person.name.title}</div>
                    <div>{this.state.person.name.first}</div>
                    <div>{this.state.person.name.last}</div>
                    <img src={this.state.person.picture.large} alt ="pic"/>
                  </div>
                }
            </div>

          );
    }
}

export default callUser;
