import React, { Component } from 'react';

class MyForm extends Component {
    state = { 
        name: "Plese enter name ",
        favPet: "the dog",
        remeberMe: false,
        title:"mr"
     }
    //  making the ipout field in react a controled field
    // meaning we are updating the state and adding to it.
     handleChange=(event, fieldName)=>{
         console.log(event.target.value)
        //  updaring the string value fildname not the state.
         this.setState({[fieldName] : event.target.value})
     }


    handleSubmit=()=>{
       console.log(this.state)
    }
    render() { 
        const {handleChange, handleChecked, handleSubmit} = this;
        const {name, favPet,title } = this.state
        return ( 
            <div>
                <input
                value={name}
                onChange={(event)=>handleChange(event, "name" )}
                />
                <textarea
                value={favPet}
                onChange={(event)=>handleChange(event, "favPet")}
                />
                <input
                type="checkbox"
                onChange={handleChecked}
                />
                <div>
                    <select value={title}
                    onChange={(event)=>handleChange(event,"title")}
                    >
                    <option>Mr</option>
                    <option>Ms</option>
                    <option>Mrs</option>
                    <option>Mr</option>
                    </select>
                </div>
                <button onClick={handleSubmit}>
                    Submit
                </button>


            </div>

         );
    }
}

export default MyForm;
