import React, { Component } from 'react';

class MyForm extends Component {
    state = { 
        name: "Plese enter name ",
        favPet: "the dog",
        remeberMe: false,
        title:"mr"
     }
    //  making the ipout field in react a controled field
    // meaning we are updating the state and adding to it.
     handleChange=(event, checkbox)=>{
         console.log(event.target.value)
        //  updating the string value fildname not the state.
         this.setState({[event.target.name]:checkbox ? event.target.checked: event.target.value})
     }

    handleSubmit=()=>{
       console.log(this.state)
    }
    render() { 
        const {handleChange, handleSubmit} = this;
        const {name, favPet,title } = this.state
        return ( 
            <div>
                <input
                name="name"
                value={name}
                onChange={handleChange}
                />
                <textarea
                name="favPet"
                value={favPet}
                onChange={handleChange}
                />
                <input
                name="checkbox"
                type="checkbox"
                onChange={handleChange}
                />
                <div>
                    <select value={title}
                    onChange={(event)=>handleChange(event,"title")}
                    >
                    <option>Mr</option>
                    <option>Ms</option>
                    <option>Mrs</option>
                    <option>Mr</option>
                    </select>
                </div>
                <button onClick={handleSubmit}>
                    Submit
                </button>


            </div>

         );
    }
}

export default MyForm;
