import React, { Component } from 'react';

class validationForm extends Component {
    state = { 
        name:'firstname',
        age:'please enter age',
        email:' Pleae enter email',
        password: '',
        nameError:'Please enter',
        ageError:''
     }

     handleChange=event=>{
         console.log(event.target.value)
         this.setState({
            [event.target.name]:event.target.value 
         })

     }

     handleSubmit=event=>{
         event.preventDefault();
         console.log(this.state)
     }

    render() { 
        const {age,password, nameError} = this.state
        const {handleChange, handleSubmit} = this
        const email = age >= 14 ? <label>Email:<input name="email" onChange={handleChange}/></label> : null
        const errorName = nameError ?  <div style ={{color:"red"}}>{nameError}</div> : null

        return ( 
            <form onSubmit={handleSubmit}>
                <input
                name="name"
                placeholder="firstname"
                onChange={handleChange}
                />

                <input
                name="age"
                placeholder="age"
                onChange={handleChange}
                />
                <p>Note only 14 and older will be allowed to input email</p>
               {email}
               <input
                name="password"
                type="password"
                placeholder="password"
                value={password}
                onChange={handleChange}
                />
                <br/>
                <button 
                type="submit"
                >
                Submit Form
                </button>
            </form>
         );
    }
}

export default validationForm;
