import React, { Component } from 'react';

class MyForm extends Component {
    state = { 
        name: "Plese enter name ",
        favPet: "the dog",
        remeberMe: false,
        title:"mr"
     }
    //  making the ipout field in react a controled field
    // meaning we are updating the state and adding to it.
     handleName=e=>{
         console.log(e.target.value)
         this.setState({name : e.target.value})
     }

     handlePet=e=>{
        console.log(e.target.value)
        this.setState({favPet : e.target.value})
    }

    handleChecked=(event)=>{
        this.setState({
           remeberMe: event.target.checked
        })
    }

    handleTitle=(event)=>{
        this.setState({
           remeberMe: event.target.value
        })
    }
    render() { 
        const {handleName, handlePet, handleChecked, handleTitle} = this;
        const {name, favPet, } = this.state
        return ( 
            <div>
                <input
                value={name}
                onChange={handleName}
                />
                <textarea
                value={favPet}
                onChange={handlePet}
                />
                <input
                type="checkbox"
                onChange={handleChecked}
                />
                <div>
                    <select value={title}>
                    <option>Mr</option>
                    <option>Ms</option>
                    <option>Mrs</option>
                    <option>Mr</option>
                    </select>
                </div>


            </div>

         );
    }
}

export default MyForm;
