import React, { Component } from 'react';

class Counter extends Component {

    state={
        count:0,
    }

    formatCount(){
        const {count} = this.state
        return count === 0  ? "Zero": this.state.count  
        }

    render() { 
        return ( 
            <React.Fragment>
                <span>{this.state.count}</span>
                <button> Increment</button>
            </React.Fragment>

         );
    }
}

export default Counter;
