class validationForm extends Component {
    state = { 
        name:'firstname',
        lastName:'',
        email:''
     }

     handleChange=event=>{
         this.setState({
            [event.target.name]:event.target.value 
         })

     }

    render() { 
        const{name} = this.state
        return ( 
            <form>
                <input
                name="name"
                value={name}
                />
                <input/>
                <input/>
            </form>
         );
    }
}

export default validationForm;
