import React, { Component } from 'react';

class ImageSlider extends Component {
    state = { 
        images : [
            "https://i.pinimg.com/236x/b6/21/07/b62107d70659823c6f409480f7435d14--watercolor-food-watercolor-print.jpg",
            "https://i.pinimg.com/236x/8a/61/c1/8a61c1ca0d1a20db922aa5e3a8493d0e--watercolour-flowers-watercolor-ideas.jpg",
            "https://i.pinimg.com/236x/86/d6/46/86d646ce2f92efd7890843ef5af9e700.jpg",
            "https://i.pinimg.com/236x/f6/4d/d7/f64dd7eb8df298195d61569b9634f982--watercolor-sketch-watercolor-kiwi.jpg"
        ],
        // helps keep track of the image number  startting form zero
        idx: 0 ,
        age:""
     }

     handleNext=()=>{
        //  setState is asynchronous by nature value will not be
        // updated straight away.
         this.setState({
            idx: this.state.idx +1
         })           
     }
     handlePrev=()=>{
        //  setState is asynchronous by nature value will not be
        // updated straight away.
         this.setState({
            idx: this.state.idx -1
         })           
     }

    render() 
    { 
        const {images, idx} =this.state
        const {handleNext, handlePrev} = this
        return ( 
            <div> 
                <input/>
                <input/>
                <img
                style={{width: 100,
                height: 100}}
                src={images[idx]}
                alt="pic"
                />
                <button
                onClick={handlePrev}

                >Prev</button>
                <button
                onClick={handleNext}
                >Next</button>

            </div>



         );
    }
}

export default  ImageSlider;
