import React, { Component } from 'react';

class MyForm extends Component {
    state = { 
        name: "Plese enter name ",
        favPet: "the dog",
        remeberMe: true,
     }
    //  making the ipout field in react a controled field
    // meaning we are updating the state and adding to it.
     handleName=e=>{
         console.log(e.target.value)
         this.setState({name : e.target.value})
     }

     handlePet=e=>{
        console.log(e.target.value)
        this.setState({favPet : e.target.value})
    }
    render() { 
        const {handleName, handlePet} = this;
        const {name, favPet} = this.state
        return ( 
            <div>
                <input
                value={name}
                onChange={handleName}
                />
                <textarea
                value={favPet}
                onChange={handlePet}
                />
            </div>

         );
    }
}

export default MyForm;
