import React, { Component } from 'react';


class TodoForm extends Component {
    state = { 
        text: ""

     }

     onchange=event=>{
         this.setState({
             [event.target.name]:event.target.value
         })
     }

    render() { 
        const {text} = this.state
        return ( 
            <div>
            <input
            name="text"
            value={text}
            placeholder="todos.."
            />
            </div>
         );
    }
}

export default TodoForm;
