import React, { Component } from 'react';

initialState = { 
    name:'firstname',
    age:'please enter age',
    email:' Pleae enter email',
    password: '',
    nameError:'',
    emailError:'',
    ageError:'',
    passwordError:''
 }

class validationForm extends Component {


     handleChange=event=>{
         console.log(event.target.value)
         this.setState({
            [event.target.name]:event.target.value 
         })

     }

     validate=()=>{
        //  let nameError ="";
         let emailError ="";
        //  let passwordError ="";

        // does not include an @ sign
         if(!this.state.email.includes('@')){
             emailError = "invalid email";
         }
        //  if = emailError then set state to "invalid email"
         if(emailError){
             this.setState({
                 emailError
             })
             return false;
         }
        // no errors return true.
         return true;
     }

     handleSubmit=event=>{
         event.preventDefault();
         const isValid = this.validate();
         if(isValid){
            console.log(this.state)
         }

     }

    render() { 
        const {age,password, nameError, ageError, passwordError, emailError} = this.state
        const {handleChange, handleSubmit} = this
        const email = age >= 14 ? <label>Email:<input name="email" onChange={handleChange}/></label> : null
        const errorName = nameError ?  <div style ={{color:"red"}}>{nameError}</div> : null
        const errorAge = ageError ?  <div style ={{color:"red"}}>{ageError}</div> : null
        const errorEmail = emailError ?  <div style ={{color:"red"}}>{emailError}</div> : null
        const errorPassword = passwordError ?  <div style ={{color:"red"}}>{password}</div> : null

        return ( 
            <form onSubmit={handleSubmit}>
                <input
                name="name"
                placeholder="firstname"
                onChange={handleChange}
                />
               {errorName}
                <input
                name="age"
                placeholder="age"
                onChange={handleChange}
                />
                {errorAge}
                <p>Note only 14 and older will be allowed to input email</p>
               {email}
               {errorEmail}
               <input
                name="password"
                type="password"
                placeholder="password"
                value={password}
                onChange={handleChange}
                />
                {errorPassword}
                <br/>
                <button 
                type="submit"
                >
                Submit Form
                </button>
            </form>
         );
    }
}

export default validationForm;
