class validationForm extends Component {
    state = { 
        name:'firstname',
        lastName:'',
        email:''
     }

     handleChange=event=>{
         console.log(event.target.value)
         this.setState({
            [event.target.name]:event.target.value 
         })

     }

     handleSubmit=event=>{
         event.preventDefult();
         console.log(this.state)
     }

    render() { 
        const {handleChange} = this
        return ( 
            <form>
                <input
                name="name"
                onChange={handleChange}
                />
                <input
                name="lastName"
                onChange={handleChange}
                />
                <input
                name="email"
                onChange={handleChange}
                />
            </form>
         );
    }
}

export default validationForm;
