import React, { Component } from 'react';

class Counter extends Component {
    state = { 
        name:"",
        email:"",
        age:""
     }

     handleName(e){
         console.log(e.target.value);
         this.setState({name: e.target.value});

     };



    render(){ 


        return ( 
           <form >
           <label>
           Name:
           <input onChange={this.handleName} 
           value={this.state.name}
           type="text"
           />
           </label>
           <label>
           email:
           <input/>
           </label>
           <label>
           age:
           <input/>
           </label>



           </form>
         );
    }
}

export default Counter;
